        content.push(<tr><td>{topic[0]}</td><td>{topic[1]}</td><td>{topic[2]}</td><td>View Topic</td><td>Delete Topic</td></tr>)
    }
    )


   return(<div  class="topicView">

        <table>
            <tr>            
                <td>Topic</td>
                <td>Description</td>
                <td>Mesage</td>
                <td>Action</td>
            </tr>

            {content}            


        </table>



   </div>);
}

export default Topics;
