import './Topics.css'

import {useState, useEffect} from 'react';




function Topics()
{


   const [topics,setTopics] = useState([]);
   let listeners = {}


    function fetchTopics()
    {
        fetch("http://localhost:5000/api/topics").then(async (res)=>{
            if(res.ok)
            {
                console.log("fetched");
               setTopics(await res.json());


            }
          });
    }



    useEffect(fetchTopics,[]);


    topics.forEach((topic)=>{
        let listener = new ROSLIB.Topic({
            ros : ros,
            name : topic[1],
            messageType : topic[2]
          });
          listeners[topic[0]] = listener;
    });


    return(<div id="topics">
              <h3>Topics</h3>

              <TopicListView topicList={topics} rerender={fetchTopics} listeners={listeners}>

              </TopicListView>
            <div id="arena">

            </div>



    </div>);
}

function TopicListView(props)
{


    function deleteTopic(topic)
    {


          fetch("http://localhost:5000/api/deletetopic?topic="+topic).then((res)=>{
            if(res.ok)
            {
               props.rerender();

            }
            else
            {
              alert("Error!");
            }
          });


    }
    function viewTopic(topic)
    {
        props.listeners[topic].subscribe((message)=>{

                console.log(message);

        });
    }

    let content = []
    props.topicList.forEach(
        (topic)=>
    {
        content.push(<tr><td>{topic[0]}</td><td>{topic[1]}</td><td>{topic[2]}</td><td onClick={viewTopic(topic[0])}>View Topic</td><td onClick={()=>{deleteTopic(topic[0])}}>Delete Topic</td></tr>)
    }
    )


   return(<div  id="topicList">

        <table>
            <tr>            
                <th>Topic</th>
                <th>Description</th>
                <th>Mesage</th>
                <th>View Topic</th>
                <th>Delete Topic</th>

            </tr>

            {content}            


        </table>



   </div>);
}

export default Topics;
