import './Topics.css'

import {useState, useEffect} from 'react';




function Topics()
{
   const [topics,setTopics] = useState([]);


    function fetchTopics()
    {
        fetch("http://localhost:5000/api/topics").then(async (res)=>{
            if(res.ok)
            {
                console.log("fetched");
               setTopics(await res.json());


            }
          });
    }

    useEffect(fetchTopics,[]);


    return(<div id="topics">
              <h3>Topics</h3>

              <TopicListView topicList={topics} rerender={fetchTopics}>

              </TopicListView>




    </div>);
}

function TopicListView(props)
{


    function deleteTopic(topic)
    {


          fetch("/api/deletetopic?topic="+topic).then((res)=>{
            if(res.ok)
            {
               props.rerender();

            }
            else
            {
              alert("Error!");
            }
          });


    }

    let content = []
    props.topicList.forEach(
        (topic)=>
    {
        content.push(<tr><td>{topic[0]}</td><td>{topic[1]}</td><td>{topic[2]}</td><td>View Topic</td><td onClick={()=>{deleteTopic(topic[0])}}>Delete Topic</td></tr>)
    }
    )


   return(<div  id="topicList">

        <table>
            <tr>            
                <th>Topic</th>
                <th>Description</th>
                <th>Mesage</th>
                <th>View Topic</th>
                <th>Delete Topic</th>

            </tr>

            {content}            


        </table>



   </div>);
}

export default Topics;
