import {useState} from 'react';




function Topics()
{
   const [topics,setTopics] = useState([]);


    function fetchTopics()
    {
        fetch("http://localhost:5000/api/topics").then(async (res)=>{
            if(res.ok)
            {
               setTopics(await res.json());

            }
          });
    }


    return(<div id="topics">
              <h3>Topics</h3>

              <TopicListView topicList={topics} rerender={fetchTopics}>

              </TopicListView>




    </div>);
}

function TopicListView(props)
{


    <a onClick={props.rerender}>Refresh</a>

   return(<div>{props.topicList}</div>);
}

export default Topics;
